WITH actor_pid as (select pid from person p where p.name='Al Pacino'), movie_list as (select distinct ar.mid as mid1 from actor_pid ap, actor_role ar where ar.pid=ap.pid), actor_list as (select distinct pid as pid1, count(ar1.mid) as cnt from actor_role ar1, movie_list ml where ar1.mid in ml.mid1 group by ar1.pid) select p3.name from person p3 where p3.pid in (select al2.pid1 from actor_list al2, actor_pid ap2 where al2.pid1 != ap2.pid and al2.cnt =(select cnt from actor_list al1, actor_pid ap1 where al1.pid1 = ap1.pid));